#### Method to hide PS window
############################################################################
function Show-Console
{
    # .Net methods for hiding/showing the console in the background
    # Usage: show-console -show / show-console -hide
    # Originally authored by @F1reF0x (github.com/F1reF0x)

    param ([Switch]$Show,[Switch]$Hide)

    if (-not ("Console.Window" -as [type])) {
        Add-Type -Name Window -Namespace Console -MemberDefinition '
        [DllImport("Kernel32.dll")]
        public static extern IntPtr GetConsoleWindow();

        [DllImport("user32.dll")]
        public static extern bool ShowWindow(IntPtr hWnd, Int32 nCmdShow);
        '
    }

    if ($Show)
    {
        $consolePtr = [Console.Window]::GetConsoleWindow()
        # Hide = 0, ShowNormal = 1, ShowMinimized = 2, ShowMaximized = 3, Maximize = 3, ShowNormalNoActivate = 4,
        # Show = 5, Minimize = 6, ShowMinNoActivate = 7, ShowNoActivate = 8, Restore = 9, ShowDefault = 10, ForceMinimized = 11
        $null = [Console.Window]::ShowWindow($consolePtr, 5)
    }

    if ($Hide)
    {
        $consolePtr = [Console.Window]::GetConsoleWindow()
        #0 hide
        $null = [Console.Window]::ShowWindow($consolePtr, 0)
    }
}


#### Password & Time set methods
############################################################################

[string]$Script:selectedComputer = ""
[bool]$Script:winLapsComputer = $false

function GetPassword
{
    # Clear current data from UI and set status
    $Script:selectedComputer = ""
    $Script:winLapsComputer = $false
    #---
    $curPwd.Text = " "
    $curPwd.Hide() # Required to hide the context menu, when no password exists.
    $TimeButton.Hide()
    $curPwdTime.Text = ""
    [void]$ListPwdHistory.Items.Clear()
    
    # Status
    $statusText.Text = "Searching ..."
    $statusText.ForeColor = "Black"
    $statusText.ToolTipText = ""
    $GoButton.Enabled = $false
    Start-Sleep -Milliseconds 25 # To show status correct.

    # Check computer name input
    [string]$inputComputerName = $compTextBox.Text
    if ([string]::IsNullOrWhiteSpace($inputComputerName))
    {
        $statusText.Text = "Failed: You must enter a computer name!"
        $statusText.ForeColor = "Red"
        return
    }

    # Get password
    try
    {
        $pwds = Get-LapsADPassword -Identity "$($inputComputerName)" -AsPlainText -IncludeHistory -ErrorAction Stop -WarningAction SilentlyContinue | Select-Object ComputerName,Password,PasswordUpdateTime,ExpirationTimestamp
    }
    catch
    {
        if ($_.Exception.Message.Contains("computer in AD"))
        {
            $statusText.Text = "Failed: Computer not found!"
            $statusText.ForeColor = "Red"
            return
        }
        else
        {
            $statusText.Text = "Something failed! - Hover for more information."
            $statusText.ForeColor = "Red"
            $statusText.ToolTipText = "$($_.Exception.Message)"
            return
        }
    }
    if ($null -eq $pwds)
    {
        $statusText.Text = "Success: No password(s) available."
        $statusText.ForeColor = "Green"
        return
    }

    # Validate current password
    if ($null -eq $pwds[0])
    {
        $statusText.Text = "Something failed! - Data set for current password is invalid."
        $statusText.ForeColor = "Red"
        return
    }
    elseif ($null -eq $pwds[0].Password -or $null -eq $pwds[0].ComputerName -or $null -eq $pwds[0].ExpirationTimestamp)
    {
        $statusText.Text = "Something failed! - Data set for current password is invalid."
        $statusText.ForeColor = "Red"
        return
    }


    # Update computer name
    $compTextBox.Text = $pwds[0].ComputerName
    $Script:selectedComputer = $pwds[0].ComputerName
    $Script:winLapsComputer = $null -ne $pwds[0].PasswordUpdateTime

    # Current password
    $curPwd.Text = $pwds[0].Password
    $curPwd.Show()
    $TimeButton.Show()
    $curPwdTime.Text = $pwds[0].ExpirationTimestamp.ToString()

    # Password history
    if ($pwds.length -gt 1)
    {
        for ($i=1; $i -lt $pwds.length; $i++) {
            if ($null -eq $pwds[$i]) {
                continue;
            }
            if ($null -eq $pwds[$i].PasswordUpdateTime) {
                continue;
            }
            if ($null -eq $pwds[$i].Password) {
                continue;
            }

            $item = New-Object System.Windows.Forms.ListViewItem($pwds[$i].PasswordUpdateTime.ToString())
            $item.SubItems.Add($pwds[$i].Password)
            [void]$ListPwdHistory.Items.Add($item)
        }
    }
    if ($ListPwdHistory.Items.Count -eq 0)
    {
        $item = New-Object System.Windows.Forms.ListViewItem("")
        $item.SubItems.Add("(No history items available.)")
        [void]$ListPwdHistory.Items.Add($item)
    }
    else {
        # Resize only if passwords were added.
        [void]$ListPwdHistory.AutoResizeColumns(2)
    }

    # Empty $pwds object
    $pwds = $null

    # Set success state
    $statusText.Text = "Success."
    $statusText.ForeColor = "Green"
}

function SetExpirationTime([ref]$pForm)
{
    # Show form
    $statusText.ToolT
    $compTextBox.ReadOnly = $true
    $goButton.Enabled = $False
    $timeButton.Enabled = $false
    $compNameText.Text = "Computer: $($Script:selectedComputer)"
    $dateTime.Value = [datetime]::Now - [timespan]::FromMinutes(1)
    $dlgResult = $timeDialog.ShowDialog($pForm)

    # Execute result action
    if ($dlgResult -eq [System.Windows.Forms.DialogResult]::OK)
    {
        [datetime]$newExpTime = $dateTime.Value
        
        # Set state
        $statusText.Text = "Changing the time ..."
        $statusText.ForeColor = "Black"
        Start-Sleep -Milliseconds 25 # To show status correct.
        
        try {
           # Execute
            if ($script:winLapsComputer) {
                # WinLaps
                Set-LapsADPasswordExpirationTime -Identity $Script:selectedComputer -WhenEffective $newExpTime -ErrorAction Stop -WarningAction SilentlyContinue
            }
            else {
                # LegacyLaps
                Reset-AdmPwdPassword -ComputerName $Script:selectedComputer -WhenEffective $newExpTime -ErrorAction Stop -WarningAction SilentlyContinue
            }
            $newCompTime = Get-LapsADPassword -Identity "$($Script:selectedComputer)" -ErrorAction SilentlyContinue -WarningAction SilentlyContinue | Select-Object ExpirationTimestamp

            #Set state
            if ($newCompTime.ExpirationTimestamp.ToString() -ne $newExpTime.ToString()) {
                # Time has not changed
                $statusText.Text = "Setting the time failed! - Reason unknown."
                $statusText.ForeColor = "Red"
                return $False
            }
            else
            {
                # Time has changed
                $statusText.Text = "Changed the time successfully."
                $statusText.ForeColor = "Green"
                Start-Sleep -Milliseconds 25 # To show status correct.
                return $true
            }
        }
        catch {
            # Exception was thrown
            $statusText.Text = "Setting the time failed! - Hover for more information."
            $statusText.ForeColor = "Red"
            $statusText.ToolTipText = "$($_.Exception.Message)"
            return $False
        }
    }
    else
    {
        # Return $false on "Cancel" button
        $statusText.Text = "Operation cancelled."
        $statusText.ForeColor = "Black"
        return $False
    }
}





