function GetAllListViewData
{
    [string] $lvData = ""
    foreach ($item in $ListPwdHistory.Items)
    {
        $lvData += $item.SubItems[0].Text + " " + $item.SubItems[1].Text + "`n"
    }
    return $lvData
}
